$p1 = $E2->element(2,1);
$p2 = $E2->element(3,1);
$p3 = nicestring([$E1->element(3,2)],["R_2"]);
$eq1 = nicestring([$e[0][0],$e[0][1]],["x_1","($x[1])"]);
BEGIN_SOLUTION
