$xnum = $d * $e - $b * $f;
$ynum = -($c)*$e + $a * $f;
$den = $a * $d - $b * $c;
$x = Compute("$xnum / $den");
$y = Compute("$ynum / $den");

$xmin = -1;
$xmax =  9;
$ymin = -1;
$ymax =  9; 
