$b2 = non_zero_random(-6,6,1);
$c2 = non_zero_random(-6,6,1);
$a3 = non_zero_random(-6,6,1);
$b3 = non_zero_random(-6,6,1);
$c3 = non_zero_random(-6,6,1);           

$A = Matrix([[$a1, $b1, $c1], [$a2,$b2,$c2], [$a3,$b3,$c3]]);
$row1 = random(1,3,1);
$row2 = $row1;
while ($row1==$row2){
  $row2=random(1,3,1);
}
$c = random(2,4)*random(-1,1,2);

if ($row1==1){
  if ($row2==2) {
    $a2=$c*$a1+$a2;
    $b2=$c*$b1+$b2;
    $c2=$c*$c1+$c2;
  }
  else {
    $a3=$c*$a1+$a3;
    $b3=$c*$b1+$b3;
    $c3=$c*$c1+$c3;
  }
}
elsif ($row1==2){
  if ($row2==1) {
    $a1=$c*$a2+$a1;
    $b1=$c*$b2+$b1;
    $c1=$c*$c2+$c1;
  }
  else {
    $a3=$c*$a2+$a3;
    $b3=$c*$b2+$b3;
    $c3=$c*$c2+$c3;
  }
}
else {
  if ($row2==2) {
    $a2=$c*$a3+$a2;
    $b2=$c*$b3+$b2;
    $c2=$c*$c3+$c2;
  }
  else {
    $a1=$c*$a3+$a1;
    $b1=$c*$b3+$b1;
    $c1=$c*$c3+$c1;
  }
}

$B = Matrix([[$a1, $b1, $c1], [$a2,$b2,$c2], [$a3,$b3,$c3]]);

Context()->variables->are(R1 => ["Real", TeX=>"R_1"], R2 => ["Real", TeX=>"R_2"], R3 => ["Real", TeX=>"R_3"]);
